import React, { useState, useEffect } from 'react';
import BlindsUp from './coverings/BlindsUp';
import GlassUp from './coverings/GlassUp';
import BlindsOpen from './coverings/BlindsOpen';
import BlindsUpOpen from './coverings/BlindsUpOpen';
import GlassSlide from './coverings/GlassSlide';
import WindowContainer from './WindowContainer';

export default () => {
    const [light, setLight] = useState(false);
    const [init, setInit] = useState(false);

    const toggleLight = () => {
        setLight(prev => !prev);
    }

    useEffect(() => {
        setInit(true)
    }, [])
    return (
        <div className={`sky ${light ? 'light-on' : ''}`} style={{overflowX: "hidden"}}>
            <div className='moon'></div>
            <div className='main'>
                <div className='spacer' style={{
                    width: '100%',
                    height: '20vh',
                }}></div>

                <WindowContainer width={340} height={'70vh'}>
                    <GlassUp />
                </WindowContainer>

                <WindowContainer width={60} widthUnits={'vw'}>
                    <BlindsOpen />
                </WindowContainer>

                <WindowContainer width={350}>
                    <BlindsUp />
                </WindowContainer>

                {/* <WindowContainer width={700}
                    >
                    <BlindsUp />
                </WindowContainer>

                <WindowContainer width={400}
                    >
                    <BlindsUpOpen />
                </WindowContainer> */}

                <WindowContainer width={56} widthUnits='vw' height={'40vh'}>
                    <GlassSlide />
                </WindowContainer>

                <div className={`light`}>
                </div>
                <div className={`light-switch ${init ? light ? 'on' : 'off' : ''}`} onClick={toggleLight}>

                </div>
            </div>
        </div>
    )
}

